import React, { useState } from "react";
import axios from "axios";

export default function NewsletterForm() {
  const [email, setEmail] = useState(null);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  function removeError() {
    document.querySelector("#errorMessage").parentNode.removeChild();
  }

  function onSubmit() {
    axios
      .post("http://localhost:4000/email", {
        emailAddress: email,
      })
      .then((res) => {
        const { status } = res;
        if (status === 200) {
          setResponse({
            status: 200,
            text: "Thank You for Subscribing",
          });
        }
        if (status === 400) {
          setError("Please");
          setInterval(removeError, 3000);
        }
      });
  }

  if (!response || response.status === 400) {
    return (
      <div className={"container"}>
        <div className={"row"}>
          <h1 style={{ color: "#FFF" }}>Newsletter Registration</h1>
          <h2 style={{ color: "#FFF" }}>Email Address:</h2>
          <input type="email" onChange={(e) => setEmail(e.target.value)} />
          <input type="submit" value="submit" onClick={() => onSubmit()} />
          {error ? (
            <div id="errorMessage">{error}</div>
          ) : (
            <div id="errorMessage"></div>
          )}
        </div>
      </div>
    );
  }
  if (response.status == 200) {
    return (
      <div>
        <h1 style={{ color: "#FFF" }}>Thanks for subscribing</h1>
      </div>
    );
  }
}
