import React, { useState } from "react";
import axios from "axios";
var qs = require("qs");

export default function NewsletterForm() {
  const [email, setEmail] = useState(null);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  function removeError() {}
  function onSubmit2() {
    var data = qs.stringify({
      emailAddress: "teste@adlkjdfa.com",
      fvv: "1",
      draftResponse: '[null, null, "461536633622265465"]',
      pageHistory: "0",
      fbzx: "461536633622265465",
    });
    var config = {
      method: "post",
      url:
        "https://docs.google.com/forms/u/0/d/e/1FAIpQLSdAbjD-uXppApTFXtN5moklEphu56036NGrsGCCw8F2CUpyvA/formResponse",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        Cookie:
          "NID=204=rq2wvALAs09_qHKG564_wbqp5OY-UXYnc7F8jBqf59lpTKIjJyijSN9f-9po924SdjIN3_UhKi6isFDHEO0KlWMnVO_BsZ6qTIcppINxes1Kl1pyIELyRvvECqbuEBIqvYywsE9llaeFKbJeppx_OvkvclBV_uNDLRCPQgnVdwM",
      },
      data: data,
    };

    axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data));
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  // function onSubmit() {
  //   axios
  //     .post("http://localhost:4000/email", {
  //       emailAddress: email,
  //     })
  //     .then((res) => {
  //       const { status } = res;
  //       if (status === 200) {
  //         setResponse({
  //           status: 200,
  //           text: "Thank You for Subscribing",
  //         });
  //       }
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //       setInterval(removeError, 3000);
  //     })
  // }

  if (!response || response.status === 400) {
    return (
      <div className={"container pt-0 pb-0 mt-0 mb-0"}>
        <div id="form" className={"row"}>
          <h1 style={{ color: "#FFF" }}>Newsletter Registration</h1>
          <h2 style={{ color: "#FFF" }}>Email Address:</h2>
          <input type="email" onChange={(e) => setEmail(e.target.value)} />
          <input type="submit" value="submit" onClick={() => onSubmit2()} />
        </div>
      </div>
    );
  }
  if (response.status == 200) {
    return (
      <div>
        <h1 style={{ color: "#FFF" }}>Thanks for subscribing</h1>
      </div>
    );
  }
}
