import React, { useState, useEffect } from "react";
import Swiper from "react-id-swiper";
import GLightbox from "glightbox";
import "swiper/swiper-bundle.css";

const SliderOne = () => {
  const [swiper, setSwiper] = useState(null);
  const [isSwiperPaused, setSwiperPaused] = useState(false);

  const goNext = () => {
    if (swiper !== null) {
      swiper.slideNext();
    }
  };

  const goPrev = () => {
    if (swiper !== null) {
      swiper.slidePrev();
    }
  };

  const params = {
    slidesPerView: 3,
    loop: true,
    speed: 1000,
    spaceBetween: 30,
    autoplay: {
      delay: 3000,
      disableOnInteraction: false,
    },
    pagination: {
      el: ".swiper-pagination",
      type: "bullets",
      clickable: true,
    },
    // Responsive breakpoints
    breakpoints: {
      1499: {
        slidesPerView: 3,
      },

      991: {
        slidesPerView: 2,
      },

      767: {
        slidesPerView: 1,
      },

      575: {
        slidesPerView: 1,
      },
    },
  };

  return (
    <section className="slider-area">
      <div className="homepage-slide1">
        <Swiper {...params} getSwiper={setSwiper}>
          <div className="single-slide-item slide-bg1">
            <div className="slide-item-table">
              <div className="slide-item-tablecell">
                <div
                  className="container"
                  style={{ margin: "0px 0px 0px 60px " }}
                >
                  <div className="row">
                    <div className="col-md-7">
                      <div className="slider-heading">
                        <p className="slider__meta">The Pave Foundation</p>
                        <h2 className="slider__title">
                          Fostering Educational Creativity
                        </h2>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="single-slide-item slide-bg2">
            <div className="slide-item-table">
              <div className="slide-item-tablecell">
                <div
                  className="container"
                  style={{ margin: "0px 0px 0px 60px " }}
                >
                  <div className="row">
                    <div className="col-md-7">
                      <div className="slider-heading">
                        <p className="slider__meta">welcome to oxpitan</p>
                        <h2 className="slider__title">
                          Lend the helping hand get involved
                        </h2>
                        <a href="#" className="theme-btn">
                          discover more
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="single-slide-item slide-bg3">
            <div className="slide-item-table">
              <div className="slide-item-tablecell">
                <div
                  className="container"
                  style={{ margin: "0px 0px 0px 60px " }}
                >
                  <div className="row">
                    <div className="col-md-7">
                      <div className="slider-heading">
                        <p className="slider__meta">welcome to oxpitan</p>
                        <h2 className="slider__title">
                          Lend the helping hand get involved
                        </h2>
                        <a href="#" className="theme-btn">
                          discover more
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Swiper>
        <div className="owl-dots">
          <div onClick={goPrev} className="owl-dot">
            <span></span>
          </div>
          <div onClick={goNext} className="owl-dot">
            <span></span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default SliderOne;
