    const [selectedOption, setSelectedOption] = useState(options[0].option);
    return (
      <select value={selectedValue} onChange={(e) => setSelectedOption(e)}>
        {options.map((e) => {
          <option key={e.option} value={e.option}>
            {e.label}
          </option>;
        })}
      </select>
    );
  }

  function handleFormSubmit() {}
  if (!response || response.status === 400) {
    return (
      <div className="col-lg-6 col-sm-12 col-xs-12 col-md-12">
        <div className="helping-form">
          <div className="section-heading d-flex flex-column align-items-center justify-contents-center">
            <div className="section-icon">
              <img src="/images/section-icon.png" alt="section-icon" />
            </div>
            <h2 className="section__title ">Volunteer</h2>
            <p className="section__meta">Register to Help </p>
          </div>
          <div className="form-shared">
            <form>
              <div className="row">
                <div className="col-lg-12">
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-controls"
                      name="firstname"
                      placeHolder="First Name"
                      onChange={(e) => setFirstName(e)}
                    />
                  </div>
                </div>
                <div className="col-lg-12">
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-controls"
                      name="firstName"
                      placeHolder="First Name"
                      onChange={(e) => setFirstName(e)}
                    />
                  </div>
                </div>

                <div className="col-lg-12">
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-controls"
                      name="lastName"
                      placeHolder="Last Name"
                      onChange={(e) => setLastName(e)}
                    />
                  </div>
                </div>
                <div className="col-lg-12">
                  <div className="form-group">
                    <input
                      type="tel"
                      className="form-controls"
                      name="phoneNumber"
                      placeHolder="Phone Number"
                      onChange={(e) => setPhone(e)}
                    />
                  </div>
                </div>
                <div className="col-lg-12">
                  <div className="form-group">
                    <input
                      type="email"
                      className="form-controls"
                      name="email"
                      placeHolder="emailAddress"
                      onChange={(e) => setEmail(e)}
                    />
                  </div>
                </div>
                <div className="col-lg-12">
                  <select
                    name="steamField"
                    onChange={(e) => setSelectedOption(e)}
                  >
                    <option value="science">Science</option>
                    <option value="technology">Technology</option>
                    <option value="engineering">Engineering</option>
                    <option value="arts">Arts</option>
                    <option value="math">Math</option>
                  </select>
                </div>

                <div className="col-lg-12">
                  <button className="theme-btn submit__btn">Submit</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }

  if (response.status === 200) {
    return (
      <div>
        <h1 style={{ color: "#FFF" }}>Thank You for Your Volunteering! b</h1>
      </div>
    );
  }
};
export default VolunteerForm;
