import React, { useState } from "react";
import axios from "axios";

export default function NewsletterForm() {
  const [email, setEmail] = useState(null);
  const [response, setResponse] = useState(null);

  function onSubmit() {
    axios
      .post("http://localhost:5000/email", {
        emailAddress: email,
      })
      .then((res) => {
        const { status } = res;
        if (status === 200) {
          setResponse({
            status: 200,
            text: "Thank You for Subscribing",
          });
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }

  if (!response || response.status === 400) {
    return (
      <div className={"container pt-0 pb-0 mt-0 mb-0"}>
        <div
          id="form-Newsletter"
          className={
            "section-heading footer-heading row d-flex flex-column justify-content-center"
          }
        >
          <h2 className="section__title text__white" style={{ color: "#FFF" }}>
            Newsletter Registration
          </h2>
          <div className="d-flex flex-row mt-2 mb-2">
            <h3
              className="section__meta h6"
              style={{ color: "#FFF", width: "150px" }}
            >
              Email Address:
            </h3>
            <input type="email" onChange={(e) => setEmail(e.target.value)} />
          </div>

          <input
            className="btn btn-primary btn-sm p-1 m-1"
            type="submit"
            style={{ width: "150px" }}
            value="submit"
            onClick={() => onSubmit()}
          />
        </div>
      </div>
    );
  }
  if (response.status === 200) {
    return (
      <div>
        <h1 style={{ color: "#FFF" }}>Thanks for subscribing</h1>
      </div>
    );
  }
}
