import React, { useState } from "react";
import axios from "axios";

const VolunteerForm = () => {
  const [name, setName] = useState(null);
  const [email, setEmail] = useState(null);
  const [phone, setPhone] = useState(null);
  const [steamField, setSteamField] = useState(null);

  function handleFormSubmit() {
    const body = {
      name,
      email,
      phone,
      steamField,
    };
    return body;
  }

  const data = [
    {
      type: "text",
      name: "name",
      placeHolder: "Full Name",
      className: "form-control",
      handleInput: (e) => setName(e),
    },
    {
      type: "email",
      name: "email",
      placeHolder: "email address",
      className: "form-control",
      handleInput: (e) => setEmail(e),
    },
    {
      type: "tel",
      name: "tel",
      className: "form-control",
      placeholder: "",
      handleInput: (e) => setPhone(e),
    },
  ];

  return (
    <div className="col-lg-6 col-sm-12 col-xs-12 col-md-12">
      <div className="helping-form">
        <div className="section-heading d-flex flex-column align-items-center justify-contents-center">
          <div className="section-icon">
            <img src="/images/section-icon.png" alt="section-icon" />
          </div>
          <h2 className="section__title ">Volunteer</h2>
          <p className="section__meta">Registration</p>
        </div>
        <div className="form-shared">
          <form>
            <div className="row">
              {data.map((el, i) => {
                return (
                  <div className="col-lg-12">
                    <div className="form-group">
                      <input
                        type={el.text}
                        className={el.className}
                        name={el.name}
                        placeHolder={el.placeHolder}
                        onChange={el.handleInput}
                      />
                    </div>
                  </div>
                );
              })}

              <div className="col-lg-12">
                <button className="theme-btn submit__btn">Submit</button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};
export default VolunteerForm;
