import React, { useState } from "react";
import axios from "axios";

const VolunteerForm = () => {
  const [name, setName] = useState(null);
  const [email, setEmail] = useState(null);
  const [phone, setPhone] = useState(null);
  const [steamField, setSteamField] = useState(null);

  function handleFormSubmit() {
    const body = {
      name,
      email,
      phone,
      steam,
    };
    return body;
  }

  const data = [
    {
      type: "text",
      name: "name",
      className: "form-control",
      handleInput: (e) => setName(e),
    },
    {
      type: "email",
      name: "email",
      className: "form-control",
      handleInput: (e) => setEmail(e),
    },
    {
      type: "tel",
      name: "tel",
      className: "form-control",
      handleInput: (e) => setPhone(e),
    },
  ];

  return (
    <div className="helping-form">
      <div className="section-heading">
        <div className="section-icon">
          <img src="/images/section-icon.png" alt="section-icon" />
        </div>
        <h2 className="section__title text__white">Volunteer</h2>
        <p className="section__meta text__white">donate us now</p>
      </div>
      <div className="form-shared">
        <form action="#">
          <div className="row">
            {data.map((el, i) => {
              return (
                <div className="col-lg-23">
                  <div className="form-group">
                    <input
                      type={el.text}
                      className={el.formControl}
                      name={el.name}
                      onChange={el.handleInput}
                    />
                  </div>
                </div>
              );
            })}

            <div className="col-lg-12">
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Insert Custom Value"
                />
              </div>
            </div>
            <div className="col-lg-12">
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Full Name"
                />
              </div>
            </div>
            <div className="col-lg-12">
              <div className="form-group">
                <input
                  type="email"
                  className="form-control"
                  placeholder="Email Address"
                />
              </div>
            </div>
            <div className="col-lg-12">
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Location"
                />
              </div>
            </div>
            <div className="col-lg-12">
              <div className="form-group">
                <textarea
                  className="textarea"
                  name="message"
                  placeholder="Leave a comment"
                ></textarea>
              </div>
            </div>
            <div className="col-lg-12">
              <button className="theme-btn submit__btn">continue now</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};
export default VolunteerForm;
